import React, { Component } from 'react';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            login:"",
            pass:"",
            loginError:"",
            passError:"",
            errorMessage:""
         }
    }
    handleChange = (e)=>{
        const {name, value} = e.target;
        this.setState({[name]:value})
        if(!this.state.login || !this.state.pass ){
            const msg = "veuillez ec"
        }
    }
    render() { 
        return ( 
            <>
            <div className="card col-6 offset-3">
            <div className="card-title text-center">
                <p className="h1">Connection</p>
            </div>
            <div className="card-body">
                <form className="p-3">
                    
                    <div className="mb-3">
                        <label for="login" className="form-label fw-bold">Identifiant :</label>
                        <input type="email" onChange={this.handleChange} className="form-control" id="login" name="login" placeholder="Votre identifiant svp!"/>
                        <div id="emailHelp"  className="form-text"></div>
                    </div>
                    <div className="mb-3">
                        <label for="pass" className="form-label fw-bold">Mot de passe :</label>
                        <input type="password" onChange={this.handleChange} className="form-control" id="pass" name="pass" placeholder="Votre mot de passe svp!"/>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        </div> 
            </> 
        );
    }
}
 
export default Login;
