import React from 'react'

 const Article = (props)=>{
    return (
        <div>
            {this.props.articles.map((article, index)=>{
            <div key={index} className="card mb-3" style="max-width: 540px;">
                <div className="row g-0">
                    <div className="col-md-4">
                        <img src={`./Images/${this.article.image}`} alt="..."/>
                    </div>
                    <div className="col-md-8">
                        <div className="card-body">
                            <h5 className="card-title">{this.article.titre}</h5>
                            <p className="card-text">{this.article.corps}</p>
                            <p className="card-text"><small className="text-muted">{this.article.auteur}</small></p>
                            <a href={this.article.lien}></a>
                        </div>
                    </div>
                    </div>
                </div>
 })}
        </div>
    )
}

export default Article;
