class Edit extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            articles : [   {id:-1, 
                image:"", 
                auteur:"", 
                lien:"", 
                titre:"", 
                corps:""
            }]
         }
    }
    render() { 
        return ( 
            <>
            {/* <div class="modal" >
                <div class="modal-dialog">
                    <div class="modal-content">
