                        <div class="mb-3">                           
                            <input type="text" class="form-control" id="titre"  name="titre" placeholder="Veuillez écrire le titre de l'article "/>
                        </div>
                            <div class="mb-3">                                
                                <textarea class="form-control" id="extrait" name="extrait" rows="3" placeholder="Veuillez écrire un extrait de l'article "></textarea>
                            </div>
                            <div class="mb-3">
                                <input type="text" class="form-control" id="image"  name="image"placeholder="Veuillez écrire le nom de l'auteur et la date de publication: Matthieu - 2021"/>                           
                                 </div>
                            
                            <div class="mb-3">
                                <input type="text" class="form-control" id="image"  name="lie"placeholder="Veuillez écrire le lien de l'article"/>                          
                                  
                            </div>
                            <button type="submit" class="btn btn-primary">Editer</button>
                        </form>
            {/*        </div>
        
                     </div>
                 </div>
             </div>*/}
            </>
         );
    }
}
 
export default Edit;
