import React, { Component } from 'react';
class Accueil extends Component {
    constructor(props) {
        super(props);
        this.state = {
            titre:`Bienvenue sur mon blog`,
            articles:{id:-1, 
                image:'', 
                auteur:'', 
                date:0,
                lien:'', 
                titre:'',
                corps:''
            },
          }
    }
    componentDidMount=()=>{
        const titre = this.state.titre;
        this.props.give(titre);
        const recupArt = localStorage.getItem('articlesKey');
        this.setState({articles:recupArt})
        
    }
    render() { 
        console.log(this.state)
        return ( 
            <>
            <div>
                <h2 className="text-center">Mes passions</h2>
               {this.state.articles.map((article,index)=>{
                    return  <div key={index} className="card mb-3">
                    <div className="row ">
                        <div className="col-md-4">
                            <img src={`./Images/${article.image}`} alt="..."  className="col-12"/>
                        </div>
                        <div className="col-md-6">
                            <div className="card-body">
                                <h5 className="card-title">{article.titre}</h5>
                                <p className="card-text">{article.corps}</p>
                                <p className="card-text"><small className="text-muted">{article.auteur} - {article.date}</small></p>
                                <a href={article.lien}>Lire la suite...</a>
                            </div>
                        </div>
                        <div className="col-2 d-flex flex-column justify-content-center text-center">
                        <div className=" mb-3 ">
                        <button onClick={()=>{props.removeClick(index)}} className="button px-3 py-2 btn-danger"><FaRegTrashAlt size={25}/><p  className="mb-0 "> supprimer </p></button>
                        </div>
                        <div className=" ">
                        <button onClick={()=>{props.editClick(index)}}className=" button mb-3 px-3 py-2 btn-success"> <FaPencilAlt size={25}/><p  className="mb-0 "> Editer </p></button>
                        </div>
                        <p className="fw-bolder">id : {article.id}</p>
                        </div>
                        </div>
                    </div>
               })}
            </div>
            </>
         );
    }
}
 
export default Accueil;
