        super(props);
        this.iniState = { 
                 
                image:"", 
                auteur:"", 
                date:0,
                lien:"", 
                titre:"",
                corps:""
            
         }
         this.state = this.inistate;
    }
    componentDidMount = ()=>{
        this.setState(this.props.artReceive)
        
    }   
