    constructor(props) {
        super(props);
        this.state = {
            titre:`Bienvenue sur mon blog`,
            articles:{id:-1, 
                image:'', 
                auteur:'', 
                date:0,
                lien:'', 
                titre:'',
                corps:''
            },
          }
    }
    componentDidMount=()=>{
        const titre = this.state.titre;
        this.props.give(titre);
        const recupArt = localStorage.getItem('articlesKey');
        this.setState({articles:recupArt})
        
    }
    render() { 
