import React, { Component } from 'react'
import { BrowserRouter, NavLink, Redirect, Route, Switch } from 'react-router-dom'
import Admin from './Admin'
import Ajout from './Ajout'
import Login from './Login'
import Logout from './Logout'
import Acceuil from './Pages/Accueil'
import Contact from './Pages/Contact'
import Presentation from './Pages/Presentation'

class Menu extends Component{
 
    constructor(props) {
        super(props);
        this.state = { 
            titre:""
         }
    }
 authenticate =()=>{
    const d = new Date();
        const test = d.toLocaleDateString();
        if(test === sessionStorage.getItem('token')){
            return true
        }else{
            return false
        }
}

recupTitle=(data)=>{
const newtitre = data;
this.setState({titre:newtitre})
}
    render(){
    return (
       <BrowserRouter>
       <header className="row">
       
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">
                <img className="img-thumbnai" src="./Images/logo.png"/>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <NavLink exact className="nav-link fw-bold" aria-current="page" to="/" >Accueil</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link fw-bold" to="/presentation" >Présentation</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link fw-bold" to="/contact">Contact</NavLink>
                        </li>
                        
                        
                    </ul>
                    <h1 className="od"> {this.state.titre}</h1>
                </div>
            </div>
            
            
                {!this.authenticate()
                    ?<NavLink className="nav-link bg-secondary text-white fw-bold" to="/login">Connection</NavLink>
                    :<div>  <NavLink className="nav-link bg-secondary text-white mb-3 fw-bold " to="/logout">Déconnection</NavLink>
                            <NavLink className="nav-link bg-secondary text-white fw-bold" to="/admin123">Administration</NavLink>
                    </div>
                }
           
        </nav>
       
       </header>
       <Switch>

       <Route path="/" exact component={Acceuil} />
       <Route path="/presentation" component={Presentation} />
       <Route path="/contact" component={Contact} />
       <Route path="/logout" component={Logout} />
       <Route path="/login" component={Login} />
       <Redirect from='/admin123/reload' to="/admin123"/>
       <Route path='/admin123' render={()=>(
            this.authenticate()
            ?(<Admin give={this.recupTitle} />)
            :(<Redirect to={Login}/>)
        )}  />
       

       </Switch>

       </BrowserRouter>
    )}
}

export default Menu
