import React from 'react'

 const Article = (props)=>{
    return (
        <>
            {props.articles.map((article, index)=>{

          return  <div key={index} className="card mb-3">
                    <div className="row ">
                        <div className="col">
                            <img src={`./Images/${article.image}`} alt="..."/>
                        </div>
                        <div className="col-8">
                            <div className="card-body">
                                <h5 className="card-title">{article.titre}</h5>
                                <p className="card-text">{article.corps}</p>
                                <p className="card-text"><small className="text-muted">{article.auteur}</small></p>
                                <a href={article.lien}>Lire la suite...</a>
                            </div>
                        </div>
                        </div>
                    </div>
 })}
        </>
    )
}

export default Article;
